import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const socket = io('http://localhost:4000'); // Замініть URL на URL вашого сервера

  useEffect(() => {
    // Підписка на подію 'message' і отримання сповіщень від сервера
    socket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Закриття з'єднання під час розмонтовки компонента
    return () => {
      socket.disconnect();
    };
  }, []);

  const sendMessage = (message) => {
    // Відправка повідомлення на сервер
    socket.emit('message', message);
  };

  return (
    <div>
      <h1>Chat</h1>
      <ul>
        {messages.map((message, index) => (
          <li key={index}>{message}</li>
        ))}
      </ul>
      <input type="text" onChange={(e) => sendMessage(e.target.value)} />
    </div>
  );
};

export default Chat;
